mapper.select:
SELECT nickname, profile_img 
FROM user_tbl22 
WHERE user_id = #{user_id}

mapper.update:
UPDATE user_tbl22 
SET nickname = #{nickname}  
WHERE user_id = #{user_id}

UPDATE user_tbl22 
SET profile_img = #{profile_img}  
WHERE user_id = #{user_id}

mapper.select:
SELECT COUNT(nickname)  
FROM user_tbl22 
WHERE nickname = #{nickname}
